import httpService from "./http.service";
import localStorageService from "./localStorage.service";
const cardEndpoint = "card/";

const cardService = {
    createCard: async (payload) => {
        const { data } = await httpService.post(cardEndpoint, payload);
        return data;
    },
    getCards: async () => {
        if (localStorageService.getUserId()) {
            const { data } = await httpService.get(cardEndpoint, {
                params: {
                    orderBy: "userId",
                    equalTo: `${localStorageService.getUserId()}`
                }
            });
            return data;
        }
    },
    removeCard: async (cardId) => {
        const { data } = await httpService.delete(cardEndpoint + cardId);
        return data;
    }
};
export default cardService;
