import { createAction, createSlice } from "@reduxjs/toolkit";
import userMessageService from "../services/userMessage.service";
const userMessageSlice = createSlice({
    name: "userMessage",
    initialState: {
        entities: null,
        isLoading: true,
        error: null
    },
    reducers: {
        userMessageRequested: (state) => {
            state.isLoading = true;
        },
        userMessageRequestFiled: (state, action) => {
            state.error = action.payload;
            state.isLoading = false;
        },
        userMessageCreated: (state, action) => {
            state.entities.push(action.payload);
        }
    }
});

const { reducer: userMessageReducer, actions } = userMessageSlice;
const {
    userMessageRequestFiled,
    userMessageCreated
} = actions;

const addUserMessageRequested = createAction("userMessage/addUserMessageRequested");

export const createUserMessage = (payload) => async (dispatch, getState) => {
    dispatch(addUserMessageRequested());
    try {
        const { content } = await userMessageService.createUserMessage(payload);
        dispatch(userMessageCreated(content));
    } catch (error) {
        dispatch(userMessageRequestFiled(error.message));
    }
};

export default userMessageReducer;
