import React, { useState } from "react";
import { useSelector } from "react-redux";
import { useParams, Redirect } from "react-router-dom";
import EditUserPage from "../components/page/editUserPage";
import UserPage from "../components/page/userPage";
import UsersListPage from "../components/page/usersListPage";
import UsersLoader from "../components/ui/hoc/usersLoader";
import { getCurrentUserId } from "../store/users";
import Header from "../components/common/header/header";
import NavBarMenu from "../components/ui/navBar/navBarMenu";

const Users = () => {
    const params = useParams();
    const { userId, edit } = params;
    const currentUserId = useSelector(getCurrentUserId());
    const [isActive, setIsActive] = useState(false);
    const toggleMenu = () => {
        setIsActive(prevState => !prevState);
    };

    return (
        <div style={{ background: "#F1F3EF", height: "100%", minHeight: "100vh", position: "relative" }}>
            {isActive ? <NavBarMenu color={"white"} /> : ""}
            <Header color="#8EB391" isActive={isActive} onHandleClick={toggleMenu}/>
            <UsersLoader>
                {userId ? (
                    edit ? (
                        userId === currentUserId ? (
                            <EditUserPage />
                        ) : (
                            <Redirect to={`/users/${currentUserId}/edit`} />
                        )
                    ) : (
                        <UserPage userId={userId} />
                    )
                ) : (
                    <UsersListPage />
                )}
            </UsersLoader>
        </div>
    );
};

export default Users;
