import React from "react";
import { useSelector } from "react-redux";
import { Link, NavLink } from "react-router-dom";
import PropTypes from "prop-types";
import { getIsLoggedIn } from "../../../store/users";
import NavProfile from "../navProfile";
import style from "./navBarMenu.module.css";

const NavBarMenu = ({ color }) => {
    const isLoggedIn = useSelector(getIsLoggedIn());
    return (
        <div style={{ display: "flex", alignItems: "center", justifyContent: "center", position: "absolute", right: 0, top: 0, bottom: 0, background: "rgb(12, 74, 52)", height: "50vh", width: "100vw", zIndex: 10 }}>
            <nav className={"navbar d-flex flex-column-reverse " + style.visible}>
                {isLoggedIn && (
                <ul className="nav d-flex flex-column" style={{ color: `${color}` }}>
                    <li className="nav-item">
                        <NavLink
                            className="nav-link "
                            aria-current="page"
                            to="/users"
                            style={{ color: "inherit" }}
                            activeClassName={style.active}
                        >
                            Users
                        </NavLink>
                    </li>
                    <li className="nav-item">
                        <NavLink
                            className="nav-link "
                            aria-current="page"
                            to="/cards"
                            style={{ color: "inherit" }}
                            activeClassName={style.active}
                        >
                            Cards
                        </NavLink>
                    </li>
                </ul>
                )}
            <div className="d-flex" style={{ color: "white" }}>
                {isLoggedIn ? (
                    <NavProfile color={color}/>
                ) : (
                    <div className="d-flex align-items-center">
                        <Link
                            className="nav-link "
                            aria-current="page"
                            to="/login"
                            style={{ color: "inherit", padding: "0.5rem 1rem" }}
                        >
                            Login
                        </Link>
                        <div> | </div>
                        <Link
                            className="nav-link "
                            aria-current="page"
                            to="/register"
                            style={{ color: "inherit", padding: "0.5rem 1rem" }}
                        >
                            Sign Up
                        </Link>
                    </div>
                )}
            </div>
        </nav>
        </div>
    );
};

NavBarMenu.propTypes = {
    color: PropTypes.string
};

export default NavBarMenu;
