import React from "react";
import PropTypes from "prop-types";
import style from "./flipcard.module.css";

const FlipCard = ({ answer, question, _id, onHandleDeleteCard, userId }) => {
    return (
        <div className={style.flipCard}>
            <div className={style.flipCardInner}>
                <div className={style.flipCardFront}>
                    <span className="fs-4" style={{ wordWrap: "break-word", maxWidth: "90%" }}>{ question }</span>
                </div>
                <div className={style.flipCardBack}>
                    <p style={{ wordWrap: "break-word", maxWidth: "90%" }}>Answer: {answer}</p>
                    <button className="btn position-absolute btn-sm top-0 end-0"
                            style={{ background: "rgb(12, 74, 52)", color: "white" }}
                            onClick={() => onHandleDeleteCard(_id)}>
                        <i className="bi bi-dash-circle"/>
                    </button>
                </div>
            </div>
        </div>
    );
};

FlipCard.propTypes = {
    question: PropTypes.string,
    answer: PropTypes.string,
    _id: PropTypes.string,
    userId: PropTypes.string,
    onHandleDeleteCard: PropTypes.func
};

export default FlipCard;
