import React, { useState } from "react";
import { useParams } from "react-router";
import LoginForm from "../components/ui/loginForm";
import RegisterForm from "../components/ui/registerForm";
import Header from "../components/common/header/header";
import NavBarMenu from "../components/ui/navBar/navBarMenu";

const Login = () => {
    const { type } = useParams();
    const [formType, setFormType] = useState(
        type === "register" ? type : "login"
    );
    const toggleFormType = (params) => {
        setFormType((prevState) =>
            prevState === "register" ? "login" : "register"
        );
    };
    const [isActive, setIsActive] = useState(false);
    const toggleMenu = () => {
        setIsActive(prevState => !prevState);
    };
    const menuClose = (e) => {
        setIsActive(false);
    };

    return (
        <div style={{ display: "flex", flexDirection: "column", position: "relative" }}>
            {isActive ? <NavBarMenu color="white"/> : ""}
            <Header color="white" isActive={isActive} onHandleClick={toggleMenu}/>
            <div onClick={menuClose} style={{ background: "#497557", minHeight: "100vh", display: "flex", alignItems: "center", position: "relative" }}>
                <div className="container mt-5">
                    <div className="row my-5">
                        <div className="col-md-6 offset-md-3 shadow p-4" style={{ background: "white" }}>
                            {formType === "register" ? (
                                <>
                                    <h3 className="mb-4">Register</h3>
                                    <RegisterForm />
                                    <p>
                                        Already have account?{" "}
                                        <a role="button" onClick={toggleFormType}>
                                            {" "}
                                            Sign In
                                        </a>
                                    </p>
                                </>
                            ) : (
                                <>
                                    <h3 className="mb-4">Login</h3>
                                    <LoginForm />
                                    <p>
                                        Do not have account?{" "}
                                        <a role="button" onClick={toggleFormType}>
                                            {" "}
                                            Sign Up
                                        </a>
                                    </p>
                                </>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};
export default Login;
