import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getCurrentUserData } from "../../store/users";
import PropTypes from "prop-types";
import Loader from "../common/loader/loader";

const NavProfile = ({ color }) => {
    const currentUser = useSelector(getCurrentUserData());
    const [isOpen, setOpen] = useState(false);
    const toggleMenu = () => {
        setOpen((prevState) => !prevState);
    };

    if (!currentUser) return <Loader/>;
    return (
        <div className="dropdown" onClick={toggleMenu}>
            <div className="btn d-flex align-items-center" style={{ color: `${color}` }}>
                <div className="me-2">{currentUser.name}</div>
                <img
                    src={currentUser.image}
                    alt="img"
                    height="40"
                    className="img-responsive rounded-circle"
                />
                { !isOpen ? <i className="bi bi-file-arrow-down" style={{ fontSize: "20px", marginLeft: "5px" }}/> : <i className="bi bi-file-arrow-up" style={{ fontSize: "20px", marginLeft: "5px" }}/> }

            </div>
            <div className={"w-100 dropdown-menu" + (isOpen ? " show" : "")}>
                <Link
                    to={`/users/${currentUser._id}`}
                    className="dropdown-item"
                >
                    Profile
                </Link>
                <Link to="/logout" className="dropdown-item">
                    Log Out
                </Link>
            </div>
        </div>
    );
};

NavProfile.propTypes = {
    color: PropTypes.string
};

export default NavProfile;
