import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCurrentUserId } from "../store/users";
import { createCard } from "../store/cards";
import AddCardForm from "../components/common/cards/addCardForm";
import CardList from "../components/common/cards/cardList";
import Modal from "../components/common/modal/Modal";
import Header from "../components/common/header/header";
import NavBarMenu from "../components/ui/navBar/navBarMenu";

const Cards = () => {
    const [modal, setModal] = useState(false);
    const dispatch = useDispatch();
    const currentUserId = useSelector(getCurrentUserId());
    const handleSubmit = (data) => {
        dispatch(createCard({ ...data, userId: currentUserId }));
        setModal(false);
    };
    const [isActive, setIsActive] = useState(false);
    const toggleMenu = () => {
        setIsActive(prevState => !prevState);
    };
    const menuClose = () => {
        setIsActive(false);
    };
    return (
        <div>
            {isActive ? <NavBarMenu color={"white"} /> : ""}
            <Header color="#8EB391" isActive={isActive} onHandleClick={toggleMenu}/>
            <div onClick={menuClose} className="container" style={{ marginTop: "80px" }}>
                <h1>Cards</h1>
                <button style={{ background: "rgb(12, 74, 52)", color: "white" }} className="btn mb-4" onClick={() => setModal(true)}>Create new flashcard</button>
                <Modal setVisible={setModal} visible={modal}>
                    <div className="card mb-2 w-100">
                        <div className="card-body ">
                            <AddCardForm onSubmit={handleSubmit} />
                        </div>
                    </div>
                </Modal>
                <CardList/>
            </div>
        </div>
    );
};

export default Cards;
